$(document).ready(function(){

	$("#header").parallax();
	$("#about").parallax();

	$(".navbar-nav li a, .menus").on("click",function(e){var t=$(this);
		$("html, body").stop().animate({scrollTop:$(t.attr("href")).offset().top},1500,"easeInOutExpo");e.preventDefault()});
	$("body").scrollspy({target:".navbar-fixed-top"});
	$(".navbar-collapse ul li a").click(function(){$(".navbar-toggle:visible").click()});

    // Email form

    $('form#messagecontact').submit(function(e){
    	e.preventDefault();
    	$.post($(this).attr('action'),$(this).serialize(),function(data){
    		$('div#messages').html(data);
    	});
    });

});